mapper.select:
SELECT * FROM mybatis.blog


        AND title = #{title}


        AND author = #{author}

SELECT * FROM mybatis.blog



            title = #{title}


            AND author = #{author}


            AND price = #{price}

mapper.update:
UPDATE mybatis.blog


        title = #{title},


        author = #{author},


        price = #{price}


WHERE id = #{id}
